  AND B.DISTRIB_LINE_NUM = E.PO_DIST_LINE_NUM
  AND A.BUSINESS_UNIT LIKE '6%'
  --AND A.REQUESTOR_ID = :1
GROUP BY  B.BUSINESS_UNIT, A.REQ_ID, B.PO_ID, E.VOUCHER_ID, D.ENTRY_STATUS 

--AP Voucher Payment Totals    
