  AND A.VOUCHER_ID = B.VOUCHER_ID
  AND B.BUSINESS_UNIT LIKE '6%'
  AND B.PYMNT_SELCT_STATUS = 'P'
  AND B.BANK_SETID = 'SHARE'
  --AND B.BANK_CD LIKE :3
  --AND B.BANK_ACCT_KEY LIKE :4
