  AND C.BANK_CD = B.BANK_CD
  AND C.BANK_ACCT_KEY = B.BANK_ACCT_KEY
  AND C.PYMNT_ID = B.PYMNT_ID
  --AND C.PYMNT_DT >= TO_DATE(:5,'YYYY-MM-DD')
  --AND C.PYMNT_DT <= TO_DATE(:6,'YYYY-MM-DD')
