--Version 1
SELECT A.SCHEDULENAME, A.JOBNAMESRC, A.DESCR, A.OPRID, C.XLATLONGNAME AS SCHEDULESTATUS, A.RUN_CNTL_ID, 
       TO_CHAR(A.STARTDATETIME, 'YYYY-MM-DD') AS START_DT, A.RECURNAME, 
