SELECT PAY.BANK_SETID, PAY.BANK_CD, PAY.BANK_ACCT_KEY, PAY.PYMNT_ID, PAY.PYMNT_METHOD, PAY.PYMNT_ID_REF
FROM PS_PAYMENT_TBL PAY
