       D.MERCHANDISE_AMT AS PO_Foreign_Amt, A.VENDOR_ID, TO_CHAR(C.DUE_DT,'YYYY-MM-DD') AS DUE_DT, B.DESCR254_MIXED, C.SHIPTO_ID, D.REQ_ID, 
