SELECT A.DEPOSIT_BU, A.DEPOSIT_ID, A.OPRID, A.ASSN_OPRID, A.BUSINESS_UNIT, A.DEPOSIT_TYPE, MAX(E.DESCRSHORT) AS "Deposit Type", 
