    AND E.SETID = J.SETID
    AND E.LEDGER_GROUP = J.LEDGER_GROUP
    AND I.SETCNTRLVALUE = A.BUSINESS_UNIT
    AND I.RECNAME = 'LED_GRP_LED_TBL'
