WHERE D.RECV_DS_STATUS = ' '
AND EXISTS (SELECT 'X' FROM PS_RECV_LN_SHIP A WHERE A.BUSINESS_UNIT = D.BUSINESS_UNIT AND A.RECEIVER_ID = D.RECEIVER_ID AND A.RECV_LN_NBR = D.RECV_LN_NBR 
