                                 AND A.BUSINESS_UNIT = C.BUSINESS_UNIT AND A.RECEIVER_ID = C.RECEIVER_ID AND (C.RECV_STATUS IN ('C', 'X') OR A.RECV_SHIP_STATUS IN ('C', 'X'))) 
