    AND B.RECEIVER_ID = C.RECEIVER_ID
    AND B.RECV_LN_NBR = C.RECV_LN_NBR
    AND B.RECV_SHIP_SEQ_NBR = C.RECV_SHIP_SEQ_NBR
    AND B.RECV_SHIP_STATUS NOT IN ('X','C')
