(
 SELECT A.ACCTG_DEF_NAME, A.SYSTEM_SOURCE, A.RECNAME, A.FIELDNAME_MON_AMT, A.FIELDNAME_ACCTDATE FROM PS_JRNLGEN_DEFN A WHERE SETID = 'SHARE'
