  AND (ER2.SETID = (SELECT SETID FROM PS_SET_CNTRL_REC WHERE SETCNTRLVALUE = IT.BUSINESS_UNIT AND RECNAME = 'ENTRY_REASN_TBL') OR ER2.SETID IS NULL)
  AND IT.BUSINESS_UNIT LIKE '6%'
  AND IT.ITEM_STATUS = 'O' 
